%=========================================================================%csvread by textscanfp=fopen('store2.csv','r');data=textscan(fp,'%n%s%s%n','delimiter', ',', 'headerLines', 1 );fclose(fp);data12=data{1,2};data13=data{1,3};data23=cell(numel(data12),1);for i=1:numel(data12)    data23{i}=[data12{i} data13{i}];end% get elemental valuestoreType=unique(data12);assrtType=unique(data12);stastType=unique(data23);% find corresponding storestoreTypeNum=length(storeType); storeTypeID=cell(storeTypeNum);assrtTypeNum=length(assrtType); assrtTypeID=cell(assrtTypeNum);stastTypeNum=length(stastType); stastTypeID=cell(stastTypeNum);%cell 2 matdata12=cell2mat(data12);data13=cell2mat(data13);%data23=cell2mat(data23);for i=1:storeTypeNum        id =find(data12==storeType{i});        storeTypeID{i}=id;endfor i=1:assrtTypeNum        id =find(data13==assrtType{i});        assrtTypeID{i}=id;endfor i=1:stastTypeNum%        id =find(strcmp(data23,stastType{i}));        id =find(strcmp(data23, stastType{i}));        stastTypeID{i}=id;endsave('data12.mat','data12');save('data13.mat','data13');save('data23.mat','data23');save('storeType.mat','storeType');save('assrtType.mat','assrtType');save('stastType.mat','stastType');save('storeTypeID.mat','storeTypeID');save('assrtTypeID.mat','assrtTypeID');save('stastTypeID.mat','stastTypeID');%=========================================================================% practical usage% find index%index = strmatch('a', storeType);