%RossmanStoreSales %Sample Solution Using nu-SVR%=========================================================================fp=fopen('test.csv','r');data=textscan(fp,'%n%n%n%D%n%n%q%q','delimiter', ',', 'headerLines', 1 );fclose(fp);f0=data{1};f1=data{2};f2=data{3};f3=datenum(data{4});f4=data{5};f5=data{6};fnum=length(data{6});f8=zeros(fnum,1);f9=zeros(fnum,1);for i=1:fnum    if(strcmp(data{7}{i},'0'))        f8(i)=0;    elseif(strcmp(data{7}{i},'a'))        f8(i)=1;    elseif(strcmp(data{7}{i},'b'))        f8(i)=2;    elseif(strcmp(data{7}{i},'c'))                f8(i)=3;    else        f8(i)=4;            end            if(strcmp(data{8}{i},'0'))        f9(i)=0;    else        f9(i)=1;            endendmaxf1=max(f1);minf3=min(f3);maxf3=max(f3);map2=zeros(maxf1,maxf3-minf3+1);map4=zeros(maxf1,maxf3-minf3+1);map5=zeros(maxf1,maxf3-minf3+1);map6=zeros(maxf1,maxf3-minf3+1);map7=zeros(maxf1,maxf3-minf3+1);map8=zeros(maxf1,maxf3-minf3+1);map9=zeros(maxf1,maxf3-minf3+1);for i=1:fnum    map2(f1(i),f3(i)-minf3+1)=f2(i);%week    map4(f1(i),f3(i)-minf3+1)=f4(i);%open    map5(f1(i),f3(i)-minf3+1)=f5(i);%promo    map8(f1(i),f3(i)-minf3+1)=f8(i);%holi1    map9(f1(i),f3(i)-minf3+1)=f9(i);%holi2    endfigure;imagesc(map2);figure;imagesc(map4);figure;imagesc(map5);figure;imagesc(map8);figure;imagesc(map9);%=========================================================================% solution4 SVM :each line modeladdpath('./libsvm-3.20/matlab');load 'map4a.mat'load 'modelStock50.mat'mean6=mean(map8)';mean7=mean(map9)';minf3=min(f3);maxf3=max(f3);pred=zeros(fnum,2);pred(:,1)=1:fnum;for i=1:fnum    model=modelStock{f1(i)};    X=[f2(i) f4(i) f5(i) f8(i) f9(i) mean6(maxf3-f3(i)+1) mean7(maxf3-f3(i)+1)];        y=1000;    [y_hat, Acc, projection] = svmpredict(y, X, model);    pred(i,2)=projection;    fprintf('%d\n',i);endcsvwrite('res.csv',pred);%=========================================================================%result visualizationmapR=zeros(maxf1,maxf3-minf3+1);for i=1:fnum    mapR(f1(i),f3(i)-minf3+1)=pred(i,2);endfigure;imagesc(mapR);